import React from "react" 
import Recharts from "recharts"
import {BarChart ,XAxis ,Tooltip,CartesianGrid,ResponsiveContainer,YAxis,Legend } from "recharts"

const Chart=(props)=>{
    const {data}=props

    return <div>
        <ResponsiveContainer width="100%" height="100%">
                            <BarChart
                            width={500}
                            height={300}
                            data={data}
                            margin={{
                                top: 5,
                                right: 30,
                                left: 20,
                                bottom: 5,
                            }}
                            >
                            <CartesianGrid strokeDasharray="3 3" />
                            <XAxis dataKey="name" />
                            <YAxis />
                            <Tooltip />
                            <Legend />
                            <Bar dataKey="pv" fill="#8884d8" />
                            <Bar dataKey="uv" fill="#82ca9d" />
                            </BarChart>
        </ResponsiveContainer>
  </div>

    // return <BarChart
