    //             data={data}
    //             margin={{ top: 5, right: 20, left: 10, bottom: 5 }}
    //         >
                
                
    //             <CartesianGrid stroke="#f5f5f5" />
    //             <XAxis dataKey="name" />
    //             <YAxix/>
    //             <Tooltip />
    //             <Bar  dataKey="pv" stroke="#ff7300" yAxisId={0} />
    //             <Bar  dataKey="uv" stroke="#387908" yAxisId={1} />
    //         </BarChart>
}

export default Chart
