import React, { useState } from 'react'
import CSVReader from 'react-csv-reader'
import { Button } from 'semantic-ui-react'
import Table from "./Table"
import Chart from "./Chart"
import Report from './Report'


const Upload = (props) => {
    // const {addItem}=props

    const [data,setData]=useState([])
    const [state,setstate]=useState(false)

    const loadFiles=(data)=>{

        const result=data.map((ele)=>{
            return { ...{name:ele["Name (Original Name)"]},
                     ...{email:ele["User Email"]},
                     ...{duration:ele["Total Duration (Minutes)"]},
                     ...{guest:ele["Guest"]} 
                  }
        }) 
        setData(result)
    } 

    const handleclick=(e)=>{

    }


    return (
        <div style={{marginTop:'40px'}}>
          <form onSubmit={handleSubmit}>
             {/* <div style={{display:'flex',justifyContent:'center'}}> */}

                <CSVReader
                    inputStyle={{color: 'red',width:'200px'}}
                    inputId="selectFile"
                    parserOptions={{ header: true }}
                    onFileLoaded={loadFiles}
                />
                <Button primary content="upload" type="submit"/>
                {/* <button onClick={handleclick}>upload</button><hr/> */}
                {data.length>0 && <Report data={data}/>}

                <div style={{display:'flex'}} >
                {data.length>0 && <Table data={data} />}
                {data.length>0 && <Chart data={data}/>}
                </div>

             {/* </div> */}
             </form>
        </div>
    )
}

export default Upload
